import { Component } from "react";
import Modal from "../Modal";
import axios from "axios";
import "../../../node_modules/bootstrap/dist/css/bootstrap.min.css";
import './index.css'

class SmsList extends Component {
  state = {
    smsList: [],
  };

  componentDidMount() {
    this.getAllStudents()
  }

  getAllStudents = () =>{
      console.log("api called");
      const response = axios
        .get("http://localhost:8080/api/students")
        .then((response) => {
          console.log(response);
          const data = response.data;
          console.log(data);
          this.setState({ smsList: data });
        })
        .catch((error) => {
          console.log("error", error);
        });
  }

  // addStudent = () =>{
  //   const {smsList} = this.state;
  //   const postResonse = axios
  //       .post("http://localhost:8080/api/students")
  //       .then((response)=>{
  //           console.log("post response",response);
  //       })
  // }

  render() {
    const { smsList } = this.state;
    return (
      <div className="sms-list-container">
        <Modal smsList = {smsList}/>
        <table className="table">
          <thead className="thead-dark">
            <tr>
              <th scope="col">#</th>
              <th scope="col">Student Name</th>
              <th scope="col">Student Address</th>
              <th scope="col">Student Email</th>
              <th scope="col">Actions</th>
            </tr>
          </thead>
          <tbody>
            {smsList.map(student=>(
                <tr key={student.id}>
                <th scope="row">{student.id}</th>
                <td>{student.studentName}</td>
                <td>{student.studentAddress}</td>
                <td>{student.studentEmail}</td>
                <td>
                    <div className="action-buttons">
                        <button className="btn btn-dark action-btns">Update</button>
                        <button className="btn btn-danger action-btns">Delete</button>
                    </div>
                </td>
              </tr>
            ))}
            
          </tbody>
        </table>
      </div>
    );
  }
}
export default SmsList;
