import { Component } from "react";
import Modal from "../Modal";
import axios from "axios";
import "../../../node_modules/bootstrap/dist/css/bootstrap.min.css";
import './index.css'

class SmsList extends Component {
  state = {
    smsList: [],
    studentName:"",
    studentAddress:"",
    studentEmail:"",
  };

  componentDidMount() {
    this.getAllStudents()
  }

  getAllStudents = () =>{
      console.log("api called");
      const response = axios
        .get("http://localhost:8080/api/students")
        .then((response) => {
          console.log(response);
          const data = response.data;
          console.log(data);
          this.setState({ smsList: data });
        })
        .catch((error) => {
          console.log("error", error);
        });
  }

  onChangeGetStudentName = (event) => {
    console.log(event.target.value);
    const studentName = event.target.value;
    this.setState({studentName:studentName});
  }
  onChangeGetStudentAddress = (event) => {
    const studentAddress = event.target.value;
    this.setState({studentAddress:studentAddress});
  }
  onChangeGetStudentEmail = (event) => {
    const studentEmail = event.target.value;
    this.setState({studentEmail:studentEmail});
  }

  addStudentApiCall = (data) =>{
    const postData = JSON.stringify(data);
    const postResonse = axios
        .post("http://localhost:8080/api/students",postData,{

          headers:{
            'Content-Type':'application/json'
          }
        })
        .then((response)=>{
            console.log("post response",response);
        })
        .catch((error)=>{
          console.error("Error adding student",error);
        })
  }

  handleSubmitForm = (event) =>{
    const {studentName,studentAddress,studentEmail} = this.state;
    const data = {studentName,studentAddress,studentEmail};
    this.addStudentApiCall(data);


  }
  render() {
    const { smsList,studentName,studentEmail,studentAddress } = this.state;
    return (
      <div className="sms-list-container">
        <Modal 
        smsList = {smsList} 
        studentName={studentName}
        studentAddress={studentAddress}
        studentEmail={studentEmail}
        onChangestudentName = {this.onChangeGetStudentName}
        onChangestudentAddress = {this.onChangeGetStudentAddress}
        onChangestudentEmail = {this.onChangeGetStudentEmail}
        handleSubmit={this.handleSubmitForm}
        />
        <table className="table">
          <thead className="thead-dark">
            <tr>
              <th scope="col">#</th>
              <th scope="col">Student Name</th>
              <th scope="col">Student Address</th>
              <th scope="col">Student Email</th>
              <th scope="col">Actions</th>
            </tr>
          </thead>
          <tbody>
            {smsList.map(student=>(
                <tr key={student.id}>
                <th scope="row">{student.id}</th>
                <td>{student.studentName}</td>
                <td>{student.studentAddress}</td>
                <td>{student.studentEmail}</td>
                <td>
                    <div className="action-buttons">
                        <button className="btn btn-dark action-btns">Update</button>
                        <button className="btn btn-danger action-btns">Delete</button>
                    </div>
                </td>
              </tr>
            ))}
            
          </tbody>
        </table>
      </div>
    );
  }
}
export default SmsList;
